#include <stdio.h>
#include <stdbool.h>
#include <string.h>
/**
 * @brief 字符串第一个唯一的字符的
 * @bug 数组类型错误
 * @param s 
 * @return int 
 */
int firstUniqChar(char *s)
{
    // 判定字符串的长度
    int len = strlen(s);
    if (len == 0 || len > 100000)
        return -1;
    // s只包含小写字母
    //char p[26] = {0};
    int p[26] = {0};
    // 统计每个字母出现的次数
    for (int i = 0; i < len; i++)
    {
        if (s[i] < 'a' || s[i] > 'z')
            return -1;
        p[s[i] - 'a']++;
    }
    // 找到第一个出现一次的字母
    for (int i = 0; i < len; i++)
    {
        if (p[s[i] - 'a'] == 1)
            return i;
    }
    return -1;
}


int main()
{
    char str[] = "";
    int result = firstUniqChar(str);
    printf("The first unique character is at index: %d\n", result);

    return 0;
}
